<div x-data="{feedbacks:$wire.entangle('feedbacks'), showFeedback : $wire.entangle('showFeedback'), showThankYou : $wire.entangle('showThankYou')  }" x-init="$watch('showThankYou', value => {
    if(value == true){
        setTimeout(function(){ showThankYou = false; }, 3000);
    }
})">
    <div
    x-transition-enter = "transform transition ease-in-out duration-1000"
    x-transition-enter-start = "translate-x-full"
    x-transition-enter-end =  "translate-x-0"
    x-transition-leave = " transform transition ease-in-out duration-500"
    x-transition-leave-start = "translate-x-0"
    x-transition-leave-end = "translate-x-full"
    x-cloak x-show="feedbacks == false && showFeedback == true" x-on:click="feedbacks = true" class="absolute px-10 pt-2 pb-2 -mr-16 text-center transform -rotate-90 bg-blue-400 rounded-t-full pointer-events-auto bottom-10 -right-1 md:flex"><h3 class="tracking-widest text-white">Feedback</h3></div>
    
    <div>
            <div class="fixed inset-0 overflow-hidden" aria-labelledby="slide-over-title" role="dialog" aria-modal="true" x-cloak x-show="feedbacks == true && showFeedback == true">
                <div class="absolute inset-0 overflow-hidden">
                <div class="absolute inset-0 transition-opacity bg-gray-500 bg-opacity-75" aria-hidden="true" x-cloak x-show="feedbacks == true && showFeedback == true"></div>
                <div class="fixed inset-y-0 right-0 flex max-w-full pl-10 pointer-events-none">
                    
                    <div  class="relative w-screen max-w-md pointer-events-auto" x-cloak x-show="feedbacks == true && showFeedback == true" x-transition:enter="transform transition ease-in-out duration-500 sm:duration-700" x-transition:enter-start="translate-x-full" x-transition:enter-end="translate-x-0" x-transition:leave="transform transition ease-in-out duration-500 sm:duration-700" x-transition:leave-start="translate-x-0" x-transition:leave-end="translate-x-full">
                                    
                        <div class="absolute right-0 flex flex-col h-56 py-6 overflow-hidden bg-white shadow-xl bottom-10 rounded-l-xl" @click.away="feedbacks = false" >
                            <div class="flex px-4 sm:px-6">
                            <h2 class="text-lg font-medium text-gray-900" id="slide-over-title">How was your experience using the site?</h2>
                            </div>
                            <div class="relative grid grid-cols-5 gap-2 px-4 mx-auto mt-6 sm:px-6">
